/** Content of a transaction message (body or init state). */

public struct TONTransactionMessageContent: Codable {

    /** The hash of the initial state */
    public var hash: String?
    public var body: TONBase64?
    /** The decoded metadata from the initial state body */
    public var decoded: AnyCodable?

    public init(hash: String? = nil, body: TONBase64? = nil, decoded: AnyCodable? = nil) {
        self.hash = hash
        self.body = body
        self.decoded = decoded
    }

    public enum CodingKeys: String, CodingKey, CaseIterable {
        case hash
        case body
        case decoded
    }

    // Encodable protocol methods

    public func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encodeIfPresent(hash, forKey: .hash)
        try container.encodeIfPresent(body, forKey: .body)
        try container.encodeIfPresent(decoded, forKey: .decoded)
    }
}



