import Foundation
import BigInt

/** Node in the transaction execution tree. */

public struct TONTransactionTraceNode: Codable {

    public var txHash: TONHex?
    public var inMsgHash: TONHex?
    /** Child trace nodes representing spawned messages */
    public var children: [TONTransactionTraceNode]

    public init(txHash: TONHex? = nil, inMsgHash: TONHex? = nil, children: [TONTransactionTraceNode]) {
        self.txHash = txHash
        self.inMsgHash = inMsgHash
        self.children = children
    }

    public enum CodingKeys: String, CodingKey, CaseIterable {
        case txHash
        case inMsgHash
        case children
    }

    // Encodable protocol methods

    public func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encodeIfPresent(txHash, forKey: .txHash)
        try container.encodeIfPresent(inMsgHash, forKey: .inMsgHash)
        try container.encode(children, forKey: .children)
    }
}



